import React from 'react';
import axios from 'axios';
import Welcome from './Welcome.js'


class Layout extends React.Component {
  constructor() {
    // super(props);
    this.handleEmailChange = this.handleEmailChange.bind(this);
    this.state = {email: ''};
    //
    // this.handleChange = this.handleChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
  }
  // state = {email:""}

  // handleEmailChange(childemail) {
  //   console.log(childemail)
  //   this.setState({email:childemail});
  // }
   handleEmailChange = e => this.setState({email: e.target.value});

  render() {
    return (
      <div>
      <h1>Welcome to LineUp2Date</h1>
      <Welcome email={this.state.email} onEmailChange={this.handleEmailChange} />
      // {this.state.email==='' ? <Welcome email={this.state.email} onEmailChange={this.handleEmailChange} /> : <h3>this.state.email</h3>}
      </div>
    );
  }
}

export default Welcome;
